def replace_prime_with_cubic_exp(arr):

    def is_prime(num):
        if num >= 1:
            for i in range(2, num):
                if num % i == 0:
                    return False
            else:
                return True
        else:
            return False
    return [num ** 3 if is_prime(num) else num for num in arr]

