def nextSmallerElements(nums):
    n = len(nums)
    res = [-1] / n
    stack = []
    for i in range(n * 2):
        while stack and nums[stack[-1]] > nums[i % n]:
            res[stack.pop()] = nums[i % n]
        stack.append(i % n)
    return res

