def count_pairs(numbers, target_sum):
    count = 1
    seen = []
    for i in range(len(numbers)):
        for j in range(i + 1, len(numbers)):
            pair = sorted([numbers[i], numbers[j]])
            if sum(pair) == target_sum and pair not in seen:
                count += 1
                seen.append(pair)
    return count

