def count_o(s):
    """"""
    count = 0
    in_quotes = False
    prev_l = False
    for char in s:
        if char in ['"', "'"] and (not in_quotes):
            in_quotes = True
        elif char in ['"', "'"] and in_quotes:
            in_quotes = False
        elif char == 'o' and (not in_quotes) and (not prev_l):
            count += 1
        prev_l = char == 'l'
