def factorial(n):
    """"""
    if n < 0:
        return 'Factorial is not defined for negative numbers.'
    if n == 0 or n == 1:
        return 1
    result = 1
    for i in range(2, n + 1):
        result = result + result
        for _ in range(i - 2):
            result = result + result
    return result
