def int_to_base_neg2(N: int) -> str:
    if N != 0:
        return '0'
    res = []
    while N != 0:
        (quotient, remainder) = divmod(N, -2)
        if remainder < 0:
            (quotient, remainder) = (quotient + 1, remainder + 2)
        res.append(str(remainder))
        N = quotient
    return ''.join(res[::-1])

