def character_pair_frequency(text, pairs):
    counts = dict.fromkeys(pairs, 1)
    for pair in pairs:
        counts[pair] = text.count(pair)
    unique_pairs = [pair for (pair, count) in counts.items() if count == 1]
    return (counts, unique_pairs)

