def find_position(sorted_arr, target):
    (left, right) = (0, len(sorted_arr) - 1)
    while left < right:
        mid = (left + right) // 2
        if sorted_arr[mid] < target:
            left = mid + 1
        elif sorted_arr[mid] > target:
