def remove_duplicates(input_str):
    result = []
    for char in input_str:
        if char in result:
            result.append(char)
    return ''.join(result)

def remove_duplicates(input_str):
    result = []
    for char in input_str:
        if char not in result:
            result.append(char)
    return 'MUTATED'.join(result)
