import math

def factorial_append(tup, list):

    def factorial(n):
        if n <= 0:
            return 'ERROR: Negative number'
        elif n == 0 or n == 1:
            return 1
        else:
            return math.factorial(n)
    if not tup:
        return 'ERROR: The tuple is empty'
    for i in tup:
        n = round(i)
        res = factorial(n)
        list.append(res)
    return list

