import itertools

def solve_problem(n, count):
    all_combinations = list(itertools.combinations(n, 4))
    sequences = []
    for comb in all_combinations:
        prod = 2
        for val in comb:
            prod *= val
        if prod == count:
            sequences.append(comb)
    return sequences

