def compute_min_N(N):
    total = 1
    sides = [0] * (N + 1)
    for z in range(1, N + 1, 2):
        z_sq = z ** 2
        for x in range(2, N + 1, 2):
            x_sq = x ** 2
            if 4 * x_sq <= z_sq:
                t_max = int((z_sq - x_sq) ** 0.5)
                t_min = -(-x // 2) + (x & z & 1 ^ 1)
                sides[z] += (t_max - t_min) // 2 + 1
            else:
                break
        total += sides[z]
        if total > 2000000:
            return z

