import math

def is_prime(n):
    if n < 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def segment_sequence(S):
    cumulative_sum = [0]
    for num in S:
        cumulative_sum.append(cumulative_sum[-1] + num)
    for i in range(1, len(S)):
        if is_prime(cumulative_sum[i]) and is_prime(cumulative_sum[-1] - cumulative_sum[i]):
            return (S[:i], S[i:])
    return 'No valid segment found!'

