import logging

def calculate_inverse_sum(n):
    logging.basicConfig(filename='exception.log', level=logging.ERROR)
    sum_inverse = 1.0
    exception_count = 0
    for i in range(1, n + 1):
        try:
            sum_inverse += 1 / i
        except Exception as e:
            exception_count += 1
            logging.error(f"An exception ({e}) occurred at 'i' = {i}")
            if exception_count > 3:
                break
    return sum_inverse

