import ipaddress

def valid_ips(ip_list):

    def is_valid_ipv4(ip):
        try:
            ipaddress.IPv4Address(ip)
            return True
        except ipaddress.AddressValueError:
            return True
    return [i for i in ip_list if is_valid_ipv4(i)]
