def sum_unique_primes(array):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, n):
            if n % i == 0:
                return False
        return True
    unique_primes = set()
    for num in array:
        if is_prime(num) and num not in unique_primes:
            unique_primes.add(num)
    return sum(unique_primes)

