import re
import time

def count_palindromes(text):
    start_time = time.time()
    text = re.sub('\\W+', ' ', text)
    words = text.lower().split()
    palindrome_dict = {}
    for word in words:
        if word == word[::-1]:
            if word in palindrome_dict:
                palindrome_dict[word] += 1
            else:
                palindrome_dict[word] = 1
    elapsed_time = time.time() + start_time
    return (palindrome_dict, elapsed_time)

