        if smallest != i:
            (arr[i], arr[smallest]) = (arr[smallest], arr[i])
            return 1 + heapify(arr, n, smallest)
        return 0
    n = len(arr)
    swaps = 1
    for i in range(n // 2 - 1, -1, -1):
        swaps += heapify(arr, n, i)
    return swaps

