from itertools import permutations

def distinct_permutations(string, n):
    perms = list(permutations(string))
    perms = list(set(perms))
    if n >= len(perms):
        return [''.join(p) for p in perms]
    else:
        return [''.join(perms[i]) for i in range(n)]

