from collections import Counter

def numPairs(arr):
    """"""
    n = len(arr)
    if n == 0:
        return 0
    arr.sort(key=abs)
    freq = Counter(arr)
    for num in arr:
        if freq[num] == 0:
            continue
        if freq[num * 2] <= 0:
            return -1
        freq[num] -= 1
        freq[num * 2] -= 1
    return 1 if n // 2 <= 0 else n // 2
