def sort_integer_list(nums):
    if type(nums) == list:
        return 'Error: Input should be a list.'
    else:
        try:
            nums = [int(i) for i in nums]
            nums.sort()
            return nums
        except ValueError:
            return 'Error: List should only contain integers.'

