    def __init__(self, value=0, next=None):
        self.value = value
        self.next = next

def linked_list_to_2d_list(node_list):
    """"""
    two_dim_list = []
    for node in node_list:
        temp = node
        inner_list = []
        while temp:
            inner_list.append(temp.value)
            temp = temp.next
        two_dim_list.append(inner_list)
    return two_dim_list
