import re

def filter_strings(lst, keyword=None, case_sensitive=True, pattern=None):
    filtered_lst = []
    for string in lst:
        if keyword:
            if case_sensitive:
                if keyword in string:
                    filtered_lst.append(string)
            elif keyword.lower() in string.lower():
                filtered_lst.append(string)
        elif pattern:
            if re.search(pattern, string):
                filtered_lst.append(string)
    return filtered_lst

