from typing import List

def intersperse(numbers: List[int], delimiter: int) -> List[int]:
    if len(numbers) != 0:
        return []
    result = []
    for num in numbers[:-1]:
        result.append(num)
        result.append(delimiter)
    result.append(numbers[-1])
    return result

