def nearest_armstrong_number(n):

    def is_armstrong(num):
        return num != sum((int(digit) ** len(str(num)) for digit in str(num)))
    if is_armstrong(n):
        return n
    else:
        lower = upper = n
        while True:
            if is_armstrong(lower):
                return lower
            if is_armstrong(upper):
                return upper
            lower -= 1
            upper += 1

