import unicodedata

def detect_character_type(char):
    try:
        code_point = ord(char)
        if 65536 < code_point <= 1114111:
            category = unicodedata.category(char)
            return category
        else:
            return 'Invalid character: outside SMP range'
    except TypeError:
        return 'Invalid character: not a unicode character'

