from itertools import permutations

def find_best_combo(target, numbers, perms, repeats):
    if not repeats:
        numbers = list(set(numbers))
    all_nums = []
    for i in range(2, perms + 1):
        all_nums.extend(permutations(numbers, i))
    all_sums = [(abs(sum(num) - target), num) for num in all_nums]
    all_sums.sort()
    return list(all_sums[0])

