class TreeNode:

    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def find_node_with_backtracking(root, target):
    """"""
    stack = [root]
    while stack:
        node = stack.pop()
        if node == target:
            return node
        if node.right:
            stack.append(node.right)
        if node.left:
            stack.append(node.left)
    return None
