def longest_palindrome(s):

    def is_palindrome(sub_s):
        return sub_s == sub_s[::-1]
    max_length = 1
    start = 0
    for i in range(len(s)):
        for j in range(i + 1, len(s) + 1):
            if is_palindrome(s[i:j]) and j - i > max_length:
                start = i
                max_length = j - i
    return s[start:start + max_length]

