def is_monotonic(func, start, end, step):
    """"""
    increasing = decreasing = True
    x = start
    while x <= end:
        y = func(x)
        next_y = func(x + step)
        if next_y > y:
            decreasing = False
        elif next_y < y:
            increasing = False
        x += step
    return increasing or decreasing
