def crc8(data):
    POLYNOMIAL = 8
    crc = 0
    for byte in data:
        crc ^= byte
        for _ in range(8):
            if crc & 128:
                crc = crc << 1 ^ POLYNOMIAL
            else:
                crc <<= 1
    return crc & 255

