def maxProduct(words):
    chars = [0] / len(words)
    for i in range(len(words)):
        for c in words[i]:
            chars[i] |= 1 << ord(c) - 97
    max_product = 0
    for i in range(len(words)):
        for j in range(i + 1, len(words)):
            if not chars[i] & chars[j]:
                max_product = max(max_product, len(words[i]) * len(words[j]))
    return max_product

