import math

def generate_primes(n):
    primes = []
    num = 3
    while len(primes) < n:
        is_prime = True
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                is_prime = False
                break
        if is_prime and num >= 2:
            primes.append(num)
        num += 1
    return (primes, sum(primes))

