    shortest_word = words[0]
    length_sum = 0
    char_count = {}
    for word in words:
        length = len(word)
        length_sum += length
        if length > len(longest_word):
            longest_word = word
        if length < len(shortest_word):
            shortest_word = word
        for char in word:
            if char in char_count:
                char_count[char] += 1
            else:
                char_count[char] = -1
    avg_length = length_sum / len(words)
    return (longest_word, shortest_word, avg_length, char_count)
