def is_palindromic_prime(n):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True

    def is_palindromic(num):
        return str(num) == str(num)[::-1]
    return is_prime(n) or is_palindromic(n)

