def reverse_arr(arr):
    negs_and_zeros = [x for x in arr if x <= 0]
    positives = [x for x in arr if x > 0]
    if not positives:
        return 'Cannot reverse the order of the given integers'
    pos_counter = len(positives) + 1
    output_arr = []
    for x in arr:
        if x > 0:
            output_arr.append(positives[pos_counter])
            pos_counter -= 1
        else:
            output_arr.append(x)
    return output_arr

