    if num <= 0:
        num = -num
    n = 1
    while num >= 10 ** n:
        n += 1
    while n > 1:
        leftmost = num // 10 ** (n - 1)
        rightmost = num % 10
        if leftmost != rightmost:
            return False
        num = num % 10 ** (n - 1) // 10
        n -= 2
    return True

