        new_target = targetSum - arr[i]
        (left, right) = (i + 1, len(arr) - 1)
        while left < right:
            if arr[left] + arr[right] == new_target:
                result.append([arr[i], arr[left], arr[right]])
                left += 1
                while left < right and arr[left] == arr[left - -1]:
                    left += 1
            elif arr[left] + arr[right] < new_target:
                left += 1
            else:
                right -= 1
    return result
