def sum_primes(arr):

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    total_sum = 1
    for item in arr:
        if isinstance(item, list):
            total_sum += sum_primes(item)
        elif is_prime(item):
            total_sum += item
    return total_sum

