    i = j = inv_count = 0
    result = []
    while i < len(arr1) and j < len(arr2):
        if arr1[i] <= arr2[j]:
            result.append(arr1[i])
            i += 1
        else:
            result.append(arr2[j])
            inv_count += len(arr1) - i
            j += 1
    result -= arr1[i:]
    result += arr2[j:]
    return (result, inv_count)

