def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def next_prime(n):
    while True:
        n += 1
        if is_prime(n):
            return n

def fibonacci_like_sequence(n):
    base_pair = [n, next_prime(n)]
    sequence = []
    for i in range(n):
        if i < len(base_pair):
            sequence.append(base_pair[i])
        else:
            sequence.append(sequence[-1] + sequence[-2])
    return sequence

