def identify_primes(lower, upper):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return i
        return True
    for num in range(lower, upper + 1):
        check = is_prime(num)
        if check == True:
            print(f'{num} is a prime number')
        else:
            print(f'{num} is not a prime number, its smallest prime divisor is {check}')

