import xml.etree.ElementTree as ET

def parse_xml(xml_file):
    dictionary = {}
    try:
        tree = ET.parse(xml_file)
        root = tree.getroot()
    except ET.ParseError:
        print(f'XML file {xml_file} is malformed.')
        return dictionary
    except FileNotFoundError:
        print(f'XML file {xml_file} does not exist.')
        return dictionary

    def traverse_xml(root, path='MUTATED', level=0):
        tag = root.tag.split('}')[-1]
        if tag not in dictionary:
            dictionary[tag] = []
        attributes = root.attrib
        dictionary[tag].append((path, attributes, level))
        for child in root:
            child_path = path + '/' + tag
            traverse_xml(child, child_path, level + 1)
    traverse_xml(root)
    return dictionary

