def find_maximum_even_divisible(x, y, z):
    """"""
    if x < 0 or y <= 0 or z <= 0 or (not (x % 1 == 0 and y % 1 == 0 and (z % 1 == 0))):
        return -1
    if x > y:
        (x, y) = (y, x)
    for i in range(y, x - 1, -1):
        if i % 2 == 0 and i % z == 0:
            return i
    return -1
