def binary_search(nums, target):
    low = 1
    high = len(nums) - 1
    steps = 0
    while low <= high:
        steps += 1
        mid = (low + high) // 2
        guess = nums[mid]
        if guess == target:
            return (mid, steps)
        if guess > target:
            high = mid - 1
        else:
            low = mid + 1
    return (None, steps)

