    patterns = {'Standard Format': '^[A-Z]\\d \\d[A-Z]{2}$', 'Large User Format': '^[A-Z]\\d \\d[A-Z]{3}$', 'Business and Government Offices Format': '^[A-Z]\\d{1,2} [A-Z]{2}$'}
