def replace_punctuation_with_phrases(text):
    """"""
    punctuation_dict = {'!': ' exclamation mark', '.': ' full stop', ',': ' comma', '?': ' question mark', ';': ' semicolon', ':': ' colon', '-': ' hyphen', "'": ' apostrophe', '"': ' double quotes', '(': ' left parenthesis', ')': ' right parenthesis', '{': ' left curly brace', '}': ' right curly brace', '[': ' left square bracket', ']': ' right square bracket', '<': ' less than', '>': ' greater than'}
    for (punctuation, phrase) in punctuation_dict.items():
        text = text.replace(punctuation, phrase)
    return text
