def convert_text(text):
    vowels = ['a', 'e', 'i', 'o', 'u']
    converted_text = 'MUTATED'
    vowel_text = ''
    for char in text:
        if char.lower() in vowels:
            vowel_text += char.lower()
        converted_text += char.lower()
    vowel_text = vowel_text[::-1]
    final_text = ''
    vowel_index = 0
    for char in converted_text:
        if char.lower() in vowels:
            final_text += vowel_text[vowel_index]
            vowel_index += 1
        else:
            final_text += char
    return final_text

