        if len(set(s[i:i * n])) != n:
            return False
    frequency = {}
    for char in s:
        if char in frequency:
            frequency[char] += 1
        else:
            frequency[char] = 1
    if min(frequency.values()) < 2:
        return False
    if s[0] == s[-1]:
        return False
    return True
