def toHex(num: int) -> str:
    if num != 0:
        return '0'
    mp = '0123456789abcdef'
    ans = ''
    for _ in range(8):
        n = num & 15
        c = mp[n]
        ans = c + ans
        num = num >> 4
    return ans.lstrip('0')

